={{  

{
  "$schema": "https://json-schema.org/draft-07/schema#",
  "title": "Executive Profile Analysis",
  "type": "object",
  "properties": {
    "executive_summary": {
      "type": "string",
      "description": "1-2 paragraphs providing introduction, current role, key highlights, and notable interests. Should establish who they are and their significance in their field or industry."
    },
    "professional_background_and_career_path": {
      "type": "string", 
      "description": "Career progression using bullet points format. Include education (e.g., '- Graduated from [University Name] with a [Degree].'), previous roles (e.g., '- Served as [Role] at [Company] from [Year] to [Year].'), and key career milestones with dates and organizations."
    },
    "role_and_impact_at_current_company": {
      "type": "string",
      "description": "Specific contributions and achievements at their current organization. Describe concrete accomplishments (e.g., 'Led the launch of [Product].', 'Oversaw expansion into [Market].'). Focus on measurable impact and strategic initiatives they've driven."
    },
    "public_presence_and_thought_leadership": {
      "type": "string",
      "description": "Summary of their public statements, recurring themes in interviews or content, speaking engagements, published articles, and thought leadership positions. Analyze their public messaging and key topics they advocate for or discuss."
    },
    "personal_insights_and_interests": {
      "type": "string",
      "description": "Personal details with verified sources only. Use bullet points (e.g., '- Enjoys marathon running.', '- Prefers classical music.'). If no verified personal information is available, state: 'Publicly available information focuses predominantly on their professional life...' Do not speculate or include unverified claims."
    }
  },
  "required": ["executive_summary", "professional_background_and_career_path", "role_and_impact_at_current_company", "public_presence_and_thought_leadership", "personal_insights_and_interests"],
  "additionalProperties": false
}

}}

={{  

{
  "$schema": "https://json-schema.org/draft-07/schema#",
  "title": "Company Analysis Document",
  "type": "object",
  "properties": {
    "executive_summary": {
      "type": "string",
      "description": "Concise overview of the company, its core business, primary GTM strategy highlights, and overall market position. Should be 1-2 paragraphs providing essential company context."
    },
    "company_overview_and_evolution": {
      "type": "string", 
      "description": "Founding details, key milestones, product features, and growth indicators. Use bullet points for founding details and milestones, present growth indicators in summarized paragraphs with source citations."
    },
    "customer_and_gtm_strategy": {
      "type": "string",
      "description": "Customer archetypes, ideal customer profiles (ICPs), go-to-market strategy, and customer acquisition channels. Cover ICP/target audience, acquisition channels with evidence, sales model, monetization, and geographic focus using descriptive paragraphs and bullet points."
    },
    "customer_experience_and_retention": {
      "type": "string",
      "description": "Onboarding processes, support systems, retention strategies, and feedback mechanisms. Use paragraphs to describe each area and highlight innovative or notable approaches to customer success."
    },
    "competitive_landscape_and_positioning": {
      "type": "string",
      "description": "Key competitors, market differentiation, and market perception. List main competitors and use paragraphs to explain how the company differentiates itself and its perceived market position."
    }
  },
  "required": ["executive_summary", "company_overview_and_evolution", "customer_and_gtm_strategy", "customer_experience_and_retention", "competitive_landscape_and_positioning"],
  "additionalProperties": false
}

}}

